import { useState } from "react";

const BasicForm = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameIsTouched, setEnteredNameIsTouched] = useState(false);

  const nameInputIsValid = enteredName.trim() !== "" && enteredNameIsTouched;

  const handleChangeInputName = (e) => {
    setEnteredName(e.target.value);
  };
  const handleBlurInputName = () => {
    setEnteredNameIsTouched(true);
  };

  const handleFormSubmission = (e) => {
    e.preventDefault();
    setEnteredNameIsTouched(true);
    if (nameInputIsValid) return;
    setEnteredName("");
  };

  const nameInputHasError = nameInputIsValid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={handleFormSubmission}>
      <div className="control-group">
        <div className={nameInputHasError}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            value={enteredName}
            onChange={handleChangeInputName}
            onBlur={handleBlurInputName}
          />
        </div>
        {!nameInputIsValid && <p>Name can't be empty</p>}
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
