import React, { useRef, useState } from "react";

const SimpleInput = (props) => {
  const nameInputRef = useRef();
  const [enteredName, SetEnteredName] = useState("");
  const [isEnteredNameVaild, setIsEnteredNameVaild] = useState(true);

  const handleInputChangeName = (e) => {
    SetEnteredName(e.target.value);
  };

  const handleFormSubmission = (e) => {
    e.preventDefault();
    if (enteredName.trim() === "") {
      setIsEnteredNameVaild(false);
      return;
    }

    const enteredInputRefValue = nameInputRef.current.value;
    console.log(enteredInputRefValue);
    SetEnteredName("");
  };

  const enteredNameInput = isEnteredNameVaild
    ? "form-control"
    : "form-control invalid";

  return (
    <form onSubmit={handleFormSubmission}>
      <div className={enteredNameInput}>
        <label htmlFor="name">Your Name</label>
        <input
          ref={nameInputRef}
          type="text"
          id="name"
          value={enteredName}
          onChange={handleInputChangeName}
        />
      </div>
      {!isEnteredNameVaild && (
        <p className="error-text">Name must not be empty</p>
      )}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
