import React, { useEffect, useState } from "react";

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);

  const enteredEmailIsValid = enteredEmail.trim() !== "";
  const enteredNameInputIsValid = enteredName.trim() !== "";
  const nameInputIsInvalid = !enteredNameInputIsValid && enteredNameTouched;

  let formIsValid = false;

  if (enteredNameInputIsValid) {
    formIsValid = true;
  }

  const handleInputChangeName = (e) => {
    setEnteredName(e.target.value);
    if (e.target.value.trim() !== "") {
    }
  };
  const handleNameInputBlur = () => {
    setEnteredNameTouched(true);
  };
  const handleFormSubmission = (e) => {
    e.preventDefault();
    setEnteredNameTouched(true);
    if (enteredName.trim() === "") {
      return;
    }

    setEnteredName("");
    setEnteredNameTouched(false);
  };

  const enteredNameClasses = nameInputIsInvalid
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={handleFormSubmission}>
      <div className={enteredNameClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          value={enteredName}
          onBlur={handleNameInputBlur}
          onChange={handleInputChangeName}
        />
      </div>
      {nameInputIsInvalid && (
        <p className="error-text">Name must not be empty</p>
      )}
      <div className="form-control">
        <label htmlFor="name">Email</label>
        <input type="email" id="email" />
      </div>

      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
