import { useBasicInput } from "../hooks/useBasicInput";

const BasicForm = (props) => {
  const {
    value: enteredName,
    isValid: nameInputIsValid,
    hasError: nameInputHasError,
    handleInputChange,
    handleInputBlur,
    reset: resetName,
  } = useBasicInput((value) => value.trim() !== "");

  // const [enteredName, setEnteredName] = useState("");
  // const [enteredNameIsTouched, setEnteredNameIsTouched] = useState(false);

  // const nameInputIsValid = enteredName.trim() !== "";

  // const handleChangeInputName = (e) => {
  //   setEnteredName(e.target.value);
  // };
  // const handleBlurInputName = () => {
  //   setEnteredNameIsTouched(true);
  // };

  const handleFormSubmission = (e) => {
    e.preventDefault();
    // setEnteredNameIsTouched(true);
    if (!nameInputIsValid) {
      return;
    }
    console.log(enteredName);
    resetName();
  };

  // const nameInputHasError = !nameInputIsValid && enteredNameIsTouched;

  const nameInputClasses = nameInputHasError
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={handleFormSubmission}>
      <div className="control-group">
        <div className={nameInputClasses}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            value={enteredName}
            onChange={handleInputChange}
            onBlur={handleInputBlur}
          />
        </div>
        {nameInputHasError && <p>Name can't be empty</p>}
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button type="submit">Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
